import React from 'react';
import { FaTrash } from 'react-icons/fa';

const TodoItem = ({ todo, onDelete }) => {
  const handleQuantityChange = (e) => {
    // Handle the quantity change, you can use this function to update the quantity in your state if needed.
    console.log(`New quantity selected: ${e.target.value}`);
  };

  return (
    <div className="todo-item">
      <input type="checkbox" checked={todo.completed} readOnly />
      <span style={{ textDecoration: todo.completed ? 'line-through' : 'none' }}>
        {todo.text}
      </span>
      {/* Quantity Dropdown */}
      <select value={todo.quantity} onChange={handleQuantityChange}>
        {[1, 2, 3, 4, 5].map((quantity) => (
          <option key={quantity} value={quantity}>
            {quantity}
          </option>
        ))}
      </select>
      <button className="trashbin" onClick={() => onDelete(todo.id)}>
        <FaTrash />
      </button>
    </div>
  );
};

export default TodoItem;
